package com.example.collegeapp;

import android.content.Intent;
import android.net.Uri;
import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.fragment.app.Fragment;
import androidx.viewpager.widget.ViewPager;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;

import com.bumptech.glide.Glide;

import java.util.ArrayList;
import java.util.List;

public class AboutFragment extends Fragment {


    private ViewPager viewPager;
    private BranchAdapter branchAdapter;
    private List<BranchModel> list;
    private ImageView map;

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
       View view =  inflater.inflate(R.layout.fragment_about, container, false);

       list = new ArrayList<>();
       list.add(new BranchModel(R.drawable.ic_computer ,"Computer Science" ,"Computer science is the study of computers and computing as well as their theoretical and practical applications. Computer science applies the principles of mathematics, engineering, and logic to a plethora of functions, including algorithm formulation, software and hardware development, and artificial intelligenc"));
       list.add(new BranchModel(R.drawable.ic_baseline_settings_24 ,"Mechanical Production" ,"Technically, mechanical engineering is the application of the principles and problem-solving techniques of engineering from design to manufacturing to the marketplace for any object."));

       branchAdapter = new BranchAdapter(getContext() , list);

       viewPager = view.findViewById(R.id.viewpager);
       viewPager.setAdapter(branchAdapter);

        map = view.findViewById(R.id.map);
        map.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                openMap();
            }
        });

        ImageView imageView = view.findViewById(R.id.college_image);

        Glide.with(getContext()).load("data:image/jpeg;base64,").into(imageView);
       return view;
    }

    private void openMap() {
        Uri uri =Uri.parse("geo:0, 0?q=IIITV-ICD");
        Intent intent =new Intent(Intent.ACTION_VIEW , uri);

        intent.setPackage("com.google.android.apps.maps");
        startActivity(intent);
    }
}